INSERT INTO `categories` (`id`, `cat_name`, `description`, `image`, `created_at`, `updated_at`, `status`) VALUES
(21, 'NICU', 'a specialized hospital unit that provides advanced medical care for newborns who are born prematurely, are critically ill, or have health complications', '1762368620_690b9c6c9b260.jpg', '2025-11-05 18:50:20', '2025-11-17 05:52:31', 1),
(22, 'LAPAROSCOPY', 'Laparoscopy is a minimally invasive surgical procedure that uses a laparoscope, a thin tube with a camera, to view the inside of the abdomen or pelvis through a small incision', '1762368691_690b9cb356829.jpg', '2025-11-05 18:51:31', '2025-11-17 12:04:57', 0),
(23, 'Endoscopy', 'Endoscopy is a medical procedure that uses an endoscope—a long, thin, flexible tube with a camera and light on the end—to examine the inside of the body for diagnostic or therapeutic purposes', '1762368820_690b9d34544b8.jpg', '2025-11-05 18:53:40', '2025-11-17 12:05:18', 0),
(24, 'Surgery Instruments', 'Surgical instruments are specialized tools used by clinicians to perform specific actions during surgery, such as cutting tissue with scalpels, grasping with forceps, or closing wounds with needles', '1762368886_690b9d76c526f.jpg', '2025-11-05 18:54:46', '2025-11-17 12:05:45', 0),
(25, 'Disposables', 'Medical disposables are single-use items, such as syringes, gloves, and masks, that are used once for a patient or procedure and then disposed of.', '1762369720_690ba0b879a68.jpg', '2025-11-05 19:08:40', NULL, 1),
(26, 'Pressure Injector', 'A medical pressure injector, also called a contrast or power injector, is a motorized device that precisely injects contrast media into a patient\'s body for enhanced visualization during imaging procedures like CT and MRI.', '1762369794_690ba102284e1.jpg', '2025-11-05 19:09:54', '2025-11-17 12:06:02', 0),
(27, 'Hospital Projects', 'A hospital project is a comprehensive plan to establish, upgrade, or renovate a healthcare facility, covering aspects from initial planning and architectural design to equipment procurement and operational implementation.', '1762369851_690ba13be7d7e.jpg', '2025-11-05 19:10:51', '2025-11-17 12:06:18', 0),
(28, 'Heart Lung Machine', 'A heart-lung machine, or cardiopulmonary bypass machine, is a device that temporarily takes over the function of the heart and lungs during open-heart surgery.', '1762369920_690ba1802f2df.jpg', '2025-11-05 19:12:00', NULL, 1),
(29, 'IABP Machine', 'An IABP machine is a medical device used to temporarily help a weakened heart pump blood more efficiently. ', '1762370045_690ba1fd73538.jpg', '2025-11-05 19:14:05', NULL, 1),
(30, 'Defibrillator', 'A defibrillator is a medical device that delivers an electric shock to the heart to correct life-threatening irregular rhythms (arrhythmias) during sudden cardiac arrest.\r\n', '1762370107_690ba23b2dc17.jpg', '2025-11-05 19:15:07', NULL, 1),
(31, 'External Pacemaker', 'An external pacemaker is a temporary medical device used to treat abnormal heart rhythms by delivering electrical pulses to maintain an adequate heart rate. ', '1762370182_690ba286950aa.jpg', '2025-11-05 19:16:22', NULL, 1),
(32, 'ECMO', 'An ECMO (Extracorporeal Membrane Oxygenation) machine is a form of life support that acts as an artificial heart and lung.', '1762370249_690ba2c983013.jpg', '2025-11-05 19:17:29', NULL, 1),
(33, 'Heater Cooler', 'A medical heater-cooler machine maintains a patient\'s temperature during surgery by heating or cooling a fluid, typically water, which is then circulated through external circuits like blankets or heat exchangers. ', '1762370312_690ba30837f31.jpg', '2025-11-05 19:18:32', NULL, 1),
(34, 'Anesthesia Machine', 'An anesthesia machine is a medical device that delivers a precise mixture of anesthetic gases, such as oxygen and inhaled anesthetics, to a patient during surgery.', '1762370378_690ba34a7bc6e.jpg', '2025-11-05 19:19:38', NULL, 1),
(35, 'Cautery Machine', 'A cautery machine is a medical device that uses heat, chemicals, or energy to cut tissue, remove abnormal tissue, or stop bleeding during surgical procedures.\r\n', '1762370454_690ba3961a532.jpg', '2025-11-05 19:20:54', '2025-11-17 12:07:03', 0),
(36, 'Vessel Sealer', 'A medical vessel sealer machine is a surgical instrument that uses a combination of pressure and energy (like bipolar electricity) to permanently seal blood vessels and other tubular structures.\r\n', '1762370535_690ba3e7f0ea3.jpg', '2025-11-05 19:22:15', '2025-11-17 12:07:20', 0),
(37, 'Harmonic Scalpel', 'A Harmonic Scalpel is a surgical device that uses ultrasonic energy to simultaneously cut and coagulate tissue.\r\n', '1762370592_690ba4203686d.jpg', '2025-11-05 19:23:12', NULL, 1),
(38, 'Ventilator', 'A medical ventilator is a machine that helps or replaces a person\'s breathing, moving air in and out of the lungs to assist with the exchange of oxygen and carbon dioxide.\r\n', '1762370642_690ba452d54e6.jpg', '2025-11-05 19:24:02', NULL, 1),
(39, 'Ventilator Portable', 'A portable ventilator is a compact, lightweight medical device that provides mechanical breathing support to patients who cannot breathe adequately on their own, allowing for mobility during transport or use in a home environment.', '1762370702_690ba48ecfa39.jpg', '2025-11-05 19:25:02', '2025-11-17 12:07:55', 0),
(40, 'Patient Monitor', 'A patient monitor machine is a medical device that continuously measures and displays a patient\'s vital signs in real-time, providing healthcare professionals with crucial data like heart rate, blood pressure, oxygen saturation (\\(SpO_{2}\\)), and body temperature.', '1762370779_690ba4dbe4564.jpg', '2025-11-05 19:26:19', NULL, 1),
(41, 'Syringe Pump', 'A medical syringe pump is a mechanical device that precisely delivers small, controlled amounts of fluids, such as medications or nutrients, to a patient over a specific time period.', '1762370835_690ba513840e7.jpg', '2025-11-05 19:27:15', '2025-11-17 12:08:28', 0),
(42, 'Infusion Pump', 'An infusion pump is a medical device that delivers fluids, such as medications, nutrients, or insulin, into a patient\'s body in controlled and precise amounts...', '1762370901_690ba5558765a.jpg', '2025-11-05 19:28:21', '2025-11-17 12:08:36', 0),
(47, 'Cardiology', 'Cardiology is the branch of medicine dedicated to the study, diagnosis, treatment, and prevention of diseases and disorders of the heart and the entire circulatory (cardiovascular) system', '1762758698_Cardiology.jpg', '2025-11-10 07:11:38', NULL, 1),
(49, 'OT', 'Operation Theatre, a sterile facility for surgical procedures, or Occupational Therapy, a healthcare service that helps patients perform daily activities', '1763367899_OT.jpg', '2025-11-17 08:24:59', NULL, 1),
(50, 'ICU', 'Patients in the ICU are monitored closely by specialized healthcare providers, who can respond immediately to life-threatening emergencies.\r\n', '1763367972_ICU.jpg', '2025-11-17 08:26:12', NULL, 1);

